import React from 'react'
import { useState, useEffect } from 'react'

const SelectionPage = () => {
  const [period,usePeriod] = useState("Montly");
  const [plans,setPlans] = useState([])
  const fetchPlans = async ()=>{
    try {
      const config = {
        headers: {
          Authorization: `Bearer ${user.token}`,
        },
      };

      const { data } = await axios.get("user/plans", config);
      setPlans(data);
    } catch (error) {
      console.log(error);
    }
  }

  useEffect(() => {
    fetchPlans(period);
  }, [period])

  return (
    <div className="container">
      <div className="box flexCol">
        <div className="heading">Choose the right plan for you</div>
        <div className="flexCol">
          <div className="flex tabs">
            <button>Montly</button>
            <button>Yearly</button>
          </div>
          <div className="underline">Montly price</div>
          <div className="underline">Video Quality</div>
          <div className="underline">Resolution</div>
          <div className="underline">Devices you can use to watch</div>
        </div>
        <div className="flexCol">

        </div>
      </div>
    </div>
  )
}

export default SelectionPage
