import React from "react";
import { SelectedDetailsState } from "../Context/SelectedDetails";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const PlanPage = () => {
  const { user,selectedPlan } = SelectedDetailsState();
  const devices = selectedPlan.devices.join("+");

  const navigate = useNavigate()

  function timeConverter(UNIX_timestamp){
    var a = new Date(UNIX_timestamp * 1000);
    var months = ['Jan','Feb','Mar','Apr','May','Jun','Jul','Aug','Sep','Oct','Nov','Dec'];
    var year = a.getFullYear();
    var month = months[a.getMonth()];
    var date = a.getDate();
    var time = month + ' ' + date + 'th, ' + year;
    return time;
  }

  const cancel = async ()=>{
    const config = {
      headers: {
        Authorization: `Bearer ${user.token}`,
      },
    };
    const res = await axios.delete('/payment',config);
    console.log(res);
  }


  useEffect(() => {
    if(!user){
      navigate('/login');
    }

  }, [])


  return (
    <div className="container flex flexCenter">
      <div className="box flexCol planPage">
        <div className="flex">
          <div>
              <div className="heading bold large">Current Plan Details</div>
              <span></span>
          </div>
          <button onClick={cancel}>Cancel</button>
        </div>
        <div className="medium">Basic</div>
        <div className="opac">{devices}</div>
        <div className="bold x-larger">
          &#8377;{selectedPlan.price}{" "}
          <span className="medium large">
            /{selectedPlan.period === "Montly" ? "mo" : "yr"}
          </span>
        </div>

        <button>Change Plan</button>
        <div className="message">Your subscription as started on {timeConverter(1691258303)} and will auto renew on {timeConverter(1691258303 + (selectedPlan.period === "Montly" ? 2716143 : 31643326))}</div>
      </div>
    </div>
  );
};

export default PlanPage;
