import React from "react";
import { useState } from "react";
import { Link } from "react-router-dom";
import axios from "axios";

const LoginPage = () => {
  const [email, setEmail] = useState();
  const [password, setPassword] = useState();

  const submitHandler = async () => {
    if (!email || !password) {
      return;
    }

    try {
      const config = {
        headers: {
          "Content-type": "application/json",
        },
      };
      const { data } = await axios.post(
        "/user/login",
        {
          email,
          password,
        },
        config
      );
      localStorage.setItem("userInfo", JSON.stringify(data));
      navigate("/selection");
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <div className="container flex">
      <div className="box flexCol login">
        <div className="heading">Login into your account</div>
        <div className="input flexCol">
          <label htmlFor="email">Email</label>
          <input
            type="email"
            placeholder="Enter your email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
        </div>
        <div className="input flexCol">
          <label htmlFor="password">Password</label>
          <input
            type="password"
            placeholder="Enter your Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </div>
        <button className="submit" onClick={submitHandler()}>Login</button>
        <div className="link">
          Already have an account? <spam> <Link to="/">Sign Up</Link> </spam>
        </div>
      </div>
    </div>
  );
};

export default LoginPage;
