import React from 'react'
import axios from "axios"
import { useState, useEffect } from 'react'

const SelectionPage = () => {
  const [period,setPeriod] = useState("Montly");
  const [plans,setPlans] = useState([])
  const fetchPlans = async ()=>{
    console.log("loading");
    try {
      const { data } = await axios.get(`user/plans?period=${period}`);
      setPlans(data);
      console.log(data);
    } catch (error) {
      console.log(error);
    }
  }

  useEffect(() => {
    fetchPlans(period);
  }, [period])

  return (
    <div className="container flex">
      <div className="box flexCol">
        <div className="heading">Choose the right plan for you</div>
        <div className="flex">
          <div className="flexCol">
            <div className="flex tabs flexCenter">
              <button className={period == "Montly" ? "tab selected" : "tab"} onClick={()=>{
                setPeriod("Montly")
              }}>Montly</button>
              <button className={period == "Yearly" ? "tab selected" : "tab"} onClick={()=>{
                setPeriod("Yearly")
              }}>Yearly</button>
            </div>
            <div className="feature underline">Montly price</div>
            <div className="feature underline">Video Quality</div>
            <div className="feature underline">Resolution</div>
            <div className="feature">Devices you can use to watch</div>
          </div>
          {
            plans.map((plan,id)=>{
              return (<div className="flexCol textCenter" key={id}>
                <div className='featureHeading'>{plan.name}</div>
                <div className="feature underline">{plan.price}</div>
                <div className="feature underline">{plan.quality}</div>
                <div className="feature underline">{plan.resolution}</div>
                <div>
                  {
                  plan.devices.map((device,id)=>{
                    return (<div className="device textCenter" key={id}>{device}</div>)
                  })
                  }
                </div>
              </div>)
            })
          }
        </div>
      </div>
    </div>
  )
}

export default SelectionPage
