import React from 'react'
import axios from "axios"
import {SelectedDetailsState} from '../Context/SelectedDetails'
import { useState, useEffect } from 'react'

const SelectionPage = () => {

  const [plans,setPlans] = useState([]);
  const fetchPlans = async ()=>{
    console.log("loading");
    try {
      const { data } = await axios.get(`user/plans?period=${period}`);
      setPlans(data);
      console.log(data);
    } catch (error) {
      console.log(error);
    }
  }

  useEffect(() => {
    fetchPlans(period);
  }, [period])

  return (
    <div className="container flex">
      <div className="box flexCol">
        <div className="heading">Choose the right plan for you</div>
        <div className="flex">
          <div className="flexCol">
            <div className="tabs flex">
              <div className="flex tabList flexCenter">
                <button className={period == "Montly" ? "tab selected" : "tab"} onClick={()=>{
                  setPeriod("Montly")
                }}>Montly</button>
                <button className={period == "Yearly" ? "tab selected" : "tab"} onClick={()=>{
                  setPeriod("Yearly")
                }}>Yearly</button>
              </div>
            </div>

            <div className="tableData fill underline">Montly price</div>
            <div className="tableData fill underline">Video Quality</div>
            <div className="tableData fill underline">Resolution</div>
            <div className="tableData fill">Devices you can use to watch</div>
          </div>
          {
            plans.map((plan,id)=>{
              return (
              <div className={selectedPlan == plan.name ?  "flexCol textCenter fill" :   "flexCol textCenter"} key={id}>
                  <div className='tableHeading flexCol flexCenter'>
                    <div className="tableHeadingData flex flexCenter">{plan.name}</div>
                    <div className="down-arrow"></div>
                  </div>
                  <div className="tableData underline">{plan.price}</div>
                  <div className="tableData underline">{plan.quality}</div>
                  <div className="tableData underline">{plan.resolution}</div>
                  <div>
                    {
                    plan.devices.map((device,id)=>{
                      return (<div className="tableData textCenter" key={id}>{device}</div>)
                    })
                    }
                  </div>
              </div>)
            })
          }
        </div>
      </div>
    </div>
  )
}

export default SelectionPage
