import React from 'react'
import axios from "axios"
import { useState, useEffect } from 'react'

const SelectionPage = () => {
  const [period,setPeriod] = useState("Montly");
  const [plans,setPlans] = useState([])
  const fetchPlans = async ()=>{
    try {
      const { data } = await axios.get(`user/plans?period=${period}`);
      setPlans(data);
      console.log(data);
    } catch (error) {
      console.log(error);
    }
  }

  useEffect(() => {
    fetchPlans(period);
  }, [setPeriod])

  return (
    <div className="container">
      <div className="box flexCol">
        <div className="heading">Choose the right plan for you</div>
        <div class="flex">
          <div className="flexCol">
            <div className="flex tabs">
              <button onClick={()=>{
                setPeriod("Montly")
              }}>Montly</button>
              <button onClick={()=>{
                setPeriod("Yearly")
              }}>Yearly</button>
            </div>
            <div className="underline">Montly price</div>
            <div className="underline">Video Quality</div>
            <div className="underline">Resolution</div>
            <div className="underline">Devices you can use to watch</div>
          </div>
          {
            plans.map((plan,id)=>{
              return (<div className="flexCol" key={id}>
                <div>{plan.name}</div>
                <div>{plan.price}</div>
                <div>{plan.quality}</div>
                <div>{plan.resolution}</div>
                <div>{plan.devices}</div>
              </div>)
            })
          }
        </div>
      </div>
    </div>
  )
}

export default SelectionPage
