import './App.css';
import { Route, Routes } from "react-router-dom";
import { useState } from 'react';
import SignupPage from './Pages/SignupPage';
import LoginPage from './Pages/LoginPage';
import SelectionPage from './Pages/SelectionPage';
import PaymentPage from './Pages/PaymentPage';
import PlanPage from './Pages/PlanPage';


function App() {

  const [user, setUser] = useState("");
  const [active,setActive] = useState(0);
  const [startTime,setStartTime] = useState();
  const [period, setPeriod] = useState("Montly");
  const [selectedPlan, setSelectedPlan] = useState(
    {
    period: "Montly",
    name: "Mobile",
    price: "100",
    quality: "Good",
    resolution: "480p",
    devices: ["Phone", "Tablet"],
  }
  );

  return (
    <>
      <Routes>
        <Route path="/" exact element={<SignupPage/>} />
        <Route path="/login" exact element={<LoginPage props={{setUser}}/>} />
        <Route path="/selection" exact element={<SelectionPage props={{user,period,setPeriod,selectedPlan,setSelectedPlan}}/>} />
        <Route path="/payment" exact element={<PaymentPage props={{user,selectedPlan,active,setActive,setStartTime}}/>} />
        <Route path="/plan" exact element={<PlanPage/>} />
      </Routes>
    </>
  );
}

export default App;
