import React, { useState } from 'react'
import {SelectedDetailsState} from '../Context/SelectedDetails'
import axios from 'axios';

const PaymentPage = () => {
    const {selectedPlan} = SelectedDetailsState();
    const [cardNumber,setCardNumber] = useState("");
    const [exp,setExp] = useState("");
    const [cvc,setCvc] = useState("");

    const submitHandler = async()=>{
        const config = {
            headers: {
              "Content-type": "application/json",
            },
          };
          const { data } = await axios.post(
            "/payment",
            {
              cardNumber,
              exp,
              cvc,
            },
            config
          );
    }
  return (
    <div className="container paymentPage flex">
        <div className="flex">
            <div className="cardDetails flexCol">
                <div className="x-larger bold">Complete Payment</div>
                <div className="opac">Enter your credit or debit
                card details</div>
                <div className="input flex">
                    <input type="text" value={cardNumber} id='cardNumber'
                    placeholder='Card Number' onChange={(e)=>{setCardNumber(e.target.value)}}/>
                    <input type="text" value={exp} id='month' placeholder='MM/YY' onChange={(e)=>{setExp(e.target.value)}}/>
                    <input type="text" value={cvc} id='cvc' placeholder='CVC' onChange={(e)=>{setCvc(e.target.value)}}/>
                </div>
                <button className="submit large" onClick={submitHandler}>Confirm Payment</button>
            </div>
            <div className="orderSummary flexCol">
                <div className="heading larger bold">Order Summary</div>
                <div className="flex underline">
                    <div className="tableData">Plan Name</div>
                    <div className="tableData medium">{selectedPlan.name}</div>
                </div>
                <div className="flex underline">
                    <div className="tableData">Billing Cycle</div>
                    <div className="tableData medium">{selectedPlan.period}</div>
                </div>
                <div className="flex">
                    <div className="tableData ">Plan price</div>
                    <div className="tableData medium">&#8377;{selectedPlan.price}/mo</div>
                </div>
            </div>
        </div>
    </div>
  )
}

export default PaymentPage
